-- ----------------------------
--  Procedure definition for `SpInputResult`
-- ----------------------------
DROP PROCEDURE IF EXISTS `SpInputResult`;
DELIMITER ;;
CREATE DEFINER=`tpg_hrms`@`localhost` PROCEDURE `SpInputResult`(
	IN `param_bulan` INT,
	IN `param_tahun` INT,
	IN `p_kd_perusahaan` INT
)
BEGIN
	delete from departemen_result where departemen_result.bulan = param_bulan and departemen_result.tahun = param_tahun;
	
	INSERT INTO `departemen_result` (`kd_dr`,bulan,tahun, `kd_departemen`, `score_kpi`)
	SELECT concat(param_tahun,param_bulan,appraisal.kd_departemen) as CODE,
				appraisal.bulan,
				appraisal.tahun,
				appraisal.kd_departemen,
				AVG (appraisal.point) score
	FROM appraisal
	INNER JOIN departemen ON appraisal.kd_departemen = departemen.kd_departemen
	where appraisal.bulan =param_bulan and appraisal.tahun =param_tahun AND departemen.kd_perusahaan = p_kd_perusahaan
	GROUP BY appraisal.kd_departemen,
				appraisal.bulan,
				appraisal.tahun;
	
-- -----------------------------------------------------------------------------------------------------------------
	delete from measurement_result where measurement_result.bulan = param_bulan and measurement_result.tahun = param_tahun;
	
	INSERT INTO `measurement_result` (`kd_mr`, `kd_measurement`,kd_bd, `bulan`, `tahun`, `weightage`,  `score`, `point_result`)
	SELECT concat(param_tahun,param_bulan,appraisal_detail.kd_measurement) as code,
				appraisal_detail.kd_measurement,
				measurement.kd_bd,
				appraisal.bulan,
				appraisal.tahun,
				round(tbl_bobot.weightage/tbl_bobot.total_weightage,4) as weightage,
				round((round(tbl_bobot.weightage/tbl_bobot.total_weightage,4)*round(AVG (appraisal_detail.point_result),2)),2) AS score,
				round(AVG (appraisal_detail.point_result),2) gross_score
	FROM appraisal
	INNER JOIN appraisal_detail ON appraisal.kd_appraisal = appraisal_detail.kd_appraisal
	INNER JOIN measurement ON measurement.kd_measurement = appraisal_detail.kd_measurement
	LEFT JOIN business_driver ON business_driver.kd_bd = measurement.kd_bd
	LEFT JOIN perspective ON business_driver.kd_perspective = perspective.kd_perspective
	LEFT JOIN (
			SELECT measurement.kd_measurement,
						measurement.nm_measurement,
						measurement.kd_bd,
						measurement_bobot.tahun,
						measurement_bobot.weightage,
						tbl_tot_bobot.total_weightage
			FROM measurement
			LEFT JOIN measurement_bobot ON measurement.kd_measurement = measurement_bobot.kd_measurement
			LEFT JOIN (
					select measurement.kd_bd, sum(measurement_bobot.weightage) as total_weightage
					from measurement_bobot
					LEFT JOIN measurement ON measurement_bobot.kd_measurement = measurement.kd_measurement
					where measurement_bobot.tahun = param_tahun
					group by measurement.kd_bd
				) as tbl_tot_bobot ON measurement.kd_bd = tbl_tot_bobot.kd_bd
			WHERE measurement_bobot.tahun = param_tahun
		) as tbl_bobot ON appraisal_detail.kd_measurement = tbl_bobot.kd_measurement
	where appraisal.bulan =param_bulan and appraisal.tahun =param_tahun and perspective.kd_perusahaan = p_kd_perusahaan
	GROUP BY appraisal.bulan,
				appraisal.tahun,
				appraisal_detail.kd_measurement,
				measurement.kd_bd,
				tbl_bobot.weightage,
				tbl_bobot.total_weightage;

-- -------------------------------------------------------------------------------------------------------------------------
	delete from business_driver_result where business_driver_result.bulan = param_bulan and business_driver_result.tahun = param_tahun;
	
	INSERT INTO `business_driver_result` (`kd_bdr`, `kd_bd`,kd_perspective, `bulan`, `tahun`, `weightage`, `score`, `result`, `point_result`)
	select concat(param_tahun,param_bulan,business_driver.kd_bd) as code,
				business_driver.kd_bd,
				business_driver.kd_perspective,
				param_bulan as bulan,
				param_tahun as tahun,
				round(business_driver_bobot.weightage/tbl_tot_bobot.total_weightage,4) as weightage,
				ifnull(round((tbl_result.result*round(business_driver_bobot.weightage/tbl_tot_bobot.total_weightage,4)),4),0) as score,
				ifnull(round(tbl_result.result,4),0) as result,
				ifnull(round(tbl_result.result,2),0) as point_result
	from business_driver
	LEFT JOIN business_driver_bobot ON business_driver.kd_bd = business_driver_bobot.kd_bd
	LEFT JOIN perspective ON business_driver.kd_perspective = perspective.kd_perspective
	LEFT JOIN (
		select business_driver.kd_perspective, sum(business_driver_bobot.weightage) as total_weightage
		from business_driver_bobot
		LEFT JOIN business_driver ON business_driver_bobot.kd_bd = business_driver.kd_bd
		where business_driver_bobot.tahun = param_tahun
		group by business_driver.kd_perspective
	) as tbl_tot_bobot ON business_driver.kd_perspective = tbl_tot_bobot.kd_perspective
	left join (
		select measurement_result.kd_bd,
					sum(measurement_result.score) result
		from measurement_result
		where measurement_result.bulan = param_bulan and measurement_result.tahun = param_tahun
		group by measurement_result.kd_bd
	)as tbl_result ON tbl_result.kd_bd = business_driver_bobot.kd_bd
	where business_driver_bobot.tahun = param_tahun and perspective.kd_perusahaan = p_kd_perusahaan;
	
-- -------------------------------------------------------------------------------------------------------------------
	delete from perspective_result where perspective_result.bulan = param_bulan and perspective_result.tahun = param_tahun;
	
	INSERT INTO `perspective_result` (`kd_pr`, `kd_perspective`, `bulan`, `tahun`, `weightage`, `score`, `result`, `point_result`)
	select 	concat(param_tahun,param_bulan,perspective.kd_perspective) as code,
				perspective.kd_perspective,
				param_bulan as bulan,
				param_tahun as tahun,
				round(perspective_bobot.weightage/tbl_total_bobot.total_weightage,4) as weightage,
				ifnull(round((tbl_result.result*round(perspective_bobot.weightage/tbl_total_bobot.total_weightage,4)),4),0) as score,
				ifnull(round(tbl_result.result,4),0) as result,
				ifnull(round(tbl_result.result,2),0) as point_result
	from (select sum(perspective_bobot.weightage) as total_weightage
			from perspective_bobot
			inner join perspective ON perspective.kd_perspective = perspective_bobot.kd_perspective
			where perspective_bobot.tahun = param_tahun and perspective.kd_perusahaan = p_kd_perusahaan
			)as tbl_total_bobot,
			perspective
	LEFT JOIN perspective_bobot ON perspective.kd_perspective = perspective_bobot.kd_perspective
	left join (
		select business_driver_result.kd_perspective,
					sum(business_driver_result.score) result
		from business_driver_result
		where business_driver_result.bulan = param_bulan and business_driver_result.tahun = param_tahun
		group by business_driver_result.kd_perspective
	)as tbl_result ON tbl_result.kd_perspective = perspective_bobot.kd_perspective
	where perspective_bobot.tahun = param_tahun and perspective.kd_perusahaan = p_kd_perusahaan;
END
;;
